sys_variable_value.value:
(function execute(inputs, outputs) {
    // Input variables are clearly named
    var tableName = inputs.table;
    var businessAct = inputs.businessActivity;

    // Use let or const for modern JavaScript practices if possible, 
    // but var is acceptable in ServiceNow's older contexts.
    var gr = new GlideRecord(tableName);
    gr.initialize();

    // Check if the businessAct input is not empty before setting (good practice)
    if (businessAct) {
        // Set the field value
        gr.setValue('service_definition', businessAct);

        // Insert the record and capture the sys_id directly
        var caseSysID = gr.insert();

        // Check if the insert was successful (caseSysID will be a valid sys_id if successful, 
        // and 'undefined' or null/empty string if not).
        if (caseSysID) {
            // The GlideRecord 'gr' is now populated with the new record's values,
            // including the auto-generated number.
            var caseNumber = gr.number; // Get the case number
            // 2. Set the values for the update
            // We do NOT need a new GlideRecord query because 'gr' is still pointing to the new record.

            // Define your update values
            var shortDesc = inputs.summary;
            var descriptionText = inputs.description;
            var wkNotes = inputs.workNotes

            // Set the new field values on the same 'gr' object
            gr.short_description = shortDesc;
            gr.description = descriptionText;
            gr.work_notes = wkNotes; 

            // 3. Perform the update operation
            gr.update();
            // Assign the sys_id to the output variable directly
            outputs.case_sys_id = caseSysID;
            outputs.case_number = caseNumber;
            // Optional: Log success or return a success message if needed
            // gs.info('New record created with sys_id: ' + caseSysID);
        } else {
            // Optional: Log failure
            // gs.error('Failed to create record in table: ' + tableName);
            outputs.case_sys_id = null; // Explicitly set to null on failure
        }
    } else {
        // Handle case where required input is missing
        // gs.warn('Missing businessActivity input for record creation.');
        outputs.case_sys_id = null;
    }

})(inputs, outputs);

